package Tema1;

public class Ejercicio11 {
	
	//Calcular area y perimetro de un circulo de radio 3,6 m
	//Formulas 	perimetro=2*Pi*r  / area=Pi*r^2

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		double r=3.6 ;
		double p , a ; 
		p=2*3.14*r ;
		a=3.14*r*r ; 
		System.out.println("Esto es el perimetro del ciruclo "+p);
		System.out.println("Esto es el area del circulo "+a);
      
	}

}
